  }
}

    Exemple de surcharge avec __call


<?php
class Caller {
  private $x = array(1, 2, 3);

  function __call($m, $a) {
    print "Méthode $m appelée :\n";
    var_dump($a);
    return $this->x;
  }
}

$foo = new Caller();
$a = $foo->test(1, "2", 3.4, true);
var_dump($a);
?>


    &example.outputs;



