refentry.refsect1:
  &reftitle.examples;



    Exemple avec apc_define_constants


<?php
$constants = array(
    'UN'   => 1,
    'DEUX'   => 2,
    'TROIS' => 3,
);
apc_define_constants('numbers', $constants);
echo UN, DEUX, TROIS;
?>


    &example.outputs;


123

