refentry.refsect1:
&reftitle.description;

 boolclasskit_method_redefine
 stringclassname
 stringmethodname
 stringargs
 stringcode
 intflags



 classkit_method_redefine retourne &true; en cas de succès ou &false;
 en cas d'échec.

&note.classkit.selfmanipulation;
&warn.experimental.func;

  &reftitle.examples;



    Exemple avec classkit_method_redefine


<?php
class Example {
    function foo() {
        return "foo!\n";
    }
}

// création d'un objet Example
$e = new Example();

// affichage Example::foo() (avant la redéfinission)
echo "Avant : " . $e->foo();

// Redéfinition de la méthode 'foo'
classkit_method_redefine(
    'Example',
    'foo',
    '',
    'return "bar!\n";',
    CLASSKIT_ACC_PUBLIC
);

// affichage de Example::foo() (après la redéfinission)
echo "Après : " . $e->foo();
?>


    &example.outputs;


Avant : foo!
Après : bar!
