    &reftitle.description;

      boolcpdf_rect
      intpdf_document
      floatx_coor
      floaty_coor
      floatwidth
      floatheight
      intmode



     cpdf_rect dessine un rectangle dont
     le coin inférieur gauche de coordonnées est
     (x_coor, y_coor).
     La largeur vaut width.
     La hauteur vaut height. &return.success;


    &cpdf.ul;




      Dessiner un rectangle


<?php

$cpdf = cpdf_open(0);
cpdf_page_init($cpdf, 1, 0, 595, 842, 1.0);

// couleur de rempissage (rouge)
cpdf_setrgbcolor($cpdf, 1, 0, 0);

// dessine un rectangle (180 * 100)
cpdf_rect($cpdf, 645, 400, 180, 100);

// colorie le rectangle
cpdf_fill($cpdf);

cpdf_finalize($cpdf);
header("Content-type: application/pdf");
cpdf_output_buffer($cpdf);
cpdf_close($cpdf);

?>
