refentry.refsect1:
  &reftitle.examples;

   Exemple avec cubrid_move_cursor


<?php
cubrid_move_cursor ($req_handle, 1, CUBRID_CURSOR_FIRST);  
// Déplace le curseur à la première place possible
$row = cubrid_fetch ($req_handle);
echo $row["id"], $row["name"];

cubrid_move_cursor ($req_handle, 1, CUBRID_CURSOR_LAST);  
// Déplace le curseur à la dernière place possible
$row = cubrid_fetch ($req_handle);
echo $row["id"], $row["name"];
?>
