refentry.refsect1:
&reftitle.description;
mixedcurl_getinfo resourcech intopt0
curl_getinfo lit les informations concernant le transfert ch.

  &reftitle.examples;



    Exemple avec curl_getinfo


<?php
// Création d'un gestionnaire curl vers une URL inexistante
$ch = curl_init('http://www.yahoo.com/');

// Exécution
curl_exec($ch);

// Vérification si une erreur est survenue
if(!curl_errno($ch))
{
	$info = curl_getinfo($ch);

	echo 'La requête a mis ' . $info['total_time'] . ' secondes à être envoyée à ' . $info['url'];
}

// Fermeture du gestionnaire
curl_close($ch);
?>
