refsect1.para:
    Si la base de données a été créée,
    un identifiant de base dbase_identifier est 
    retourné, sinon, &false;est retourné.

     Création d'une base dBase


<?php
// "database" name
  $dbname = "/tmp/test.dbf";
// database "definition"
  $def =
    array(
        array("date",     "D"),
        array("name",     "C",  50),
        array("age",      "N",   3, 0),
        array("email",    "C", 128),
        array("ismember", "L")
    );
// cr&eacute;ation
  if (!dbase_create($dbname, $def))
    print "<strong>Erreur!</strong>";
?>
