refentry.refsect1:
&reftitle.parameters;




   filename



     Le chemin où l'on doit sauvegarder le document HTML.

  &reftitle.examples;



    Sauvegarde d'un arbre HTML dans un fichier


<?php

$doc = new DOMDocument('1.0');
// nous voulons un joli affichage
$doc->formatOutput = true;

$root = $doc->createElement('html');
$root = $doc->appendChild($root);

$head = $doc->createElement('head');
$head = $root->appendChild($head);

$title = $doc->createElement('title');
$title = $head->appendChild($title);

$text = $doc->createTextNode('Ceci est le titre');
$text = $title->appendChild($text);

echo 'Ecrit : ' . $doc->saveHTMLFile("/tmp/test.html") . ' octets'; // Ecrit : 129 octets

?>
