    Récupération de tous les attributs d'un noeud


<?php

include("example.inc");

if (!$dom = domxml_open_mem($xmlstr)) {
     echo "Erreur lors de l'analyse du document\n";
     exit;
}

$root = $dom->document_element();
$attrs = $root->attributes();

echo 'Attributs de ' . $root->node_name() . "\n";
foreach ($attrs as $attribute) {
     echo ' - ' . $attribute->name . ' : ' . $attribute->value . "\n";
}

?>



    &example.outputs;


