refentry.refsect1:
  &reftitle.parameters;




     variable



       Valeur à filtrer.




     filter



       Identifiant du filtre à utiliser
       (voir la page du manuel ).




     options



       Tableau associatif d'options ou des drapeaux. Si le filtre accepte les options,
       les drapeaux peuvent être fournis dans le champ "flags" du tableau.
       Pour les fonctions de rappel, le type callback devra être
       passé. La fonction de rappel doit acceptée un seul argument ; la valeur à
       filtrer et retournera la valeur, une fois le filtre appliqué.





<?php
// Pour les filtres acceptant les options,
// utilisez ce format
$options = array(
    'options' => array(
        'default' => 3, // valeur à retourner si le filtre échoue
        // autres options ici...
        'min_range' => 0
    ),
    'flags' => FILTER_FLAG_ALLOW_OCTAL,
);
$var = filter_var('0755', FILTER_VALIDATE_INT, $options);

// Pour les filtres qui n'acceptent que des drapeaux,
// vous pouvez les passez directement
$var = filter_var('oops', FILTER_VALIDATE_BOOLEAN, FILTER_NULL_ON_FAILURE);

// Pour les filtres qui n'acceptent que des drapeaux,
// vous pouvez également les passer sous forme de tableau
$var = filter_var('oops', FILTER_VALIDATE_BOOLEAN,
                  array('flags' => FILTER_NULL_ON_FAILURE));

// Fonction de rappel du filtre
function foo($value)
{
    $ret = new stdClass;
    $ret->value = filter_var($value, FILTER_VALIDATE_BOOLEAN,
                             array('flags' => FILTER_NULL_ON_FAILURE));
    return $ret;
}
$var = filter_var('yes', FILTER_CALLBACK, array('options' => 'foo'));
?>

  &reftitle.examples;



    Exemple avec filter_var


<?php
var_dump(filter_var('bob@example.com', FILTER_VALIDATE_EMAIL));
var_dump(filter_var('example.com', FILTER_VALIDATE_URL, FILTER_FLAG_SCHEME_REQUIRED));
?>


    &example.outputs;


string(15) "bob@example.com"
bool(false)
