refentry.refsect1:
   &reftitle.description;

      boolftp_rename
      resourceftp_stream
      stringfrom
      stringto



    ftp_rename renomme le fichier ou le dossier
    from en to,
    sur le serveur ftp_stream.




      Exemple avec ftp_rename


<?php
$old_file = 'somefile.txt.bak';
$new_file = 'somefile.txt';

/* Connexion */
$conn_id = ftp_connect($ftp_server);

/* Identification */
$login_result = ftp_login($conn_id, $ftp_user_name, $ftp_user_pass);

/* Renomme le fichier $olf_file en $new_file */
if (ftp_rename($conn_id, $old_file, $new_file)) {
 echo "Le nom du fichier $old_file a été remplacé par $new_file avec succès.\n";
} else {
 echo "Echec du remplacement du nom du fichier $old_file en $new_file.\n";
}

/* Fermeture de la connexion */
ftp_close($conn_id);
?>






     &return.success;
