reference:
 FTP
 FTP



   &reftitle.intro;


   FTP : File Transfer Protocol (Protocole de transfert de fichiers).
   Ces fonctions implémentent un client pour accéder aux serveurs
   FTP, comme défini dans &spec.ftp;.





    &reftitle.required;
    &no.requirement;


   &reference.ftp.configure;



   &reftitle.runtime;
    &no.config;




   &reftitle.resources;


     This extension uses one resource type, which is the link identifier
     of the FTP connection.



   &reference.ftp.constants;



    &reftitle.examples;



      Exemple FTP


<?php
// création de la connexion
$conn_id = ftp_connect($ftp_server); 

// authentification avec nom de compte et mot de passe
$login_result = ftp_login($conn_id, $ftp_user_name, $ftp_user_pass); 

// vérification de la connexion
if ((!$conn_id) || (!$login_result)) { 
        echo "La connexion FTP a échoué!";
        echo "Tentative de connexion à $ftp_server avec $ftp_user_name";
        die; 
    } else {
        echo "Connected to $ftp_server, for user $ftp_user_name";
    }

// téléchargement d'un fichier
$upload = ftp_put($conn_id, $destination_file, $source_file, FTP_BINARY); 

// Vérification de téléchargement
if (!$upload) { 
        echo "Le téléchargement FTP a échoué!";
    } else {
        echo "Téléchargement de $source_file sur $ftp_server en $destination_file";
    }

// fermeture de la connexion FTP.
ftp_close($conn_id); 
?>







