refsect1.para:
     Cette fonction assigne un nom à un jeu de résultats. Ce nom peut être
     utilisé plus tard dans les requêtes de type UPDATE|DELETE ... WHERE 
     CURRENT OF name.
     &return.success;



<?php
    $result = ibase_query( "SELECT champ1, champ2 FROM matable FOR UPDATE" );
	   ibase_name_result($result, "my_cursor");

	   $updateqry = ibase_prepare("UPDATE matable SET champ2 = ? WHERE CURRENT OF my_cursor");

   	for ($i = 0; ibase_fetch_row($result); ++$i) {
	       ibase_execute($updateqry, $i);
	   }
?>
