refentry.refsect1:
&reftitle.description;
mixediptcembed stringiptcdata stringjpeg_file_name intspool
iptcembed intègre des données binaires IPTC dans une image JPEG.

  &reftitle.examples;

   Exemple avec iptcembed


<?php

// Fonction iptc_make_tag() par Thies C. Arntzen
function iptc_make_tag($rec, $data, $value)
{
    $length = strlen($value);
    $retval = chr(0x1C) . chr($rec) . chr($data);

    if($length < 0x8000)
    {
        $retval .= chr($length >> 8) .  chr($length & 0xFF);
    }
    else
    {
        $retval .= chr(0x80) . 
                   chr(0x04) . 
                   chr(($length >> 24) & 0xFF) . 
                   chr(($length >> 16) & 0xFF) . 
                   chr(($length >> 8) & 0xFF) . 
                   chr($length & 0xFF);
    }

    return $retval . $value;
}

// Chemin vers le fichier JPEG
$path = './phplogo.jpg';

// Nous devons vérifier s'il y a des données IPTC dans l'image JPEG. S'il y en
// a, alors nous stoppons le script car nous ne pouvons intégrer une image
// contenant déjà des données IPTC !
$image = getimagesize($path, $info);

if(isset($info['APP13']))
{
    die('Erreur : Données IPTC trouvées dans l\'image, nous ne pouvons continuer');
}

// Définit le drapeau IPTC
$iptc = array(
    '2#120' => 'Test image',
    '2#116' => 'Copyright 2008-2009, The PHP Group'
);

// Convertion du drapeau IPTC en code binaire
$data = '';

foreach($iptc as $tag => $string)
{
    $tag = substr($tag, 2);
    $data .= iptc_make_tag(2, substr($tag, 2), $string);
}

// Intégration des données IPTC
$content = iptcembed($data, $path);
?>
