$mbox = imap_open("{your.imap.host}","mailadmin","password",OP_HALFOPEN)
      or die("Impossible de se connecter : ".imap_last_error());

$quota_values = imap_get_quota($mbox, "user.kalowsky");
if(is_array($quota_values)) {
   $storage = $quota_values['STORAGE'];
   print "Utilisation actuelle de la capacité de stockage : " .  $storage['usage'];
   print "Quota actuel de stockage : " .  $storage['limit'];

   $message = $quota_values['MESSAGE']; 
   print "Niveau d'utilisation de MESSAGE : " .  $message['usage'];
   print "Quota de MESSAGE : " .  $message['limit'];

   /* ...  */ 
} 

imap_close($mbox);
