refentry.refsect1:
&reftitle.returnvalues;


 Un tableau d'entier compatible avec localtime : il contient l'heure
 sur 24 heures dans le champ tm_hour.

  &reftitle.examples;

    Exemple avec datefmt_localtime, procédural


<?php

$fmt = datefmt_create( "en_US" ,IntlDateFormatter::FULL,IntlDateFormatter::FULL,'America/Los_Angeles',IntlDateFormatter::GREGORIAN  );
$date = "Wednesday, December 31, 1969 4:00:00 PM PT";
$position = 0;
$arr = datefmt_localtime( $fmt, $date ,$position);
echo "Résultat de l'analyse : \n";
if ($arr) {
    foreach ($arr as $key => $value) {
        echo "$key : $value \n";
    }
}

?>




    Exemple avec datefmt_localtime, POO


<?php

$fmt = datefmt_create( "en_US" ,IntlDateFormatter::FULL,IntlDateFormatter::FULL,'America/Los_Angeles',IntlDateFormatter::GREGORIAN  );
$date = "Wednesday, December 31, 1969 4:00:00 PM PT";
$position = 0;
$arr = $fmt->localtime( $fmt, $date ,$position);
echo "Résultat de l'analyse : \n";
if ($arr) {
    foreach ($arr as $key => $value) {
        echo "$key : $value \n";
    }
}

?>



     &example.outputs;


tm_sec : 0 
tm_min : 0 
tm_hour : 16 
tm_year : 69 
tm_mday : 31 
tm_wday : 3 
tm_yday : 365 
tm_mon : 11 
tm_isdst : 0
