 &reftitle.examples;



   Surveillance de STDIN en utilisant l'API basique


<?php

function callback_func($fd, $events, $arg)
{
    static $max_requests;

    $max_requests++;

    if ($max_requests == 10) { /* sortie de la boucle après 10 écritures */
        event_base_loopexit($arg[1]);
    }

    /* Lecture tant que possible */
    while (false !=== ($data = fread($fd, 4096))) {
        echo $data;
    }
}

/* Création de la base et de l'événement */
$base = event_base_new();
$event = event_new();

$fd = STDIN;

/* Définit les drapeaux de l'événements */
event_set($event, $fd, EV_READ | EV_PERSIST, "callback_func", array($event, $base));
/* Définit la base de l'événement */
event_base_set($event, $base);

/* Activation de l'événement */
event_add($event);
/* Début de la boucle de l'événement */
event_base_loop($base);

?>







   Surveillance de STDIN en utilisant l'API des événements mis en tampon


<?php

function callback_func($buf, $arg)
{
    static $max_requests;

    $max_requests++;

    if ($max_requests == 10) {
        event_base_loopexit($arg);
    }

    /* Lecture tant que possible */
    while (false !=== ($data = event_buffer_read($buf, 10))) {
        echo $data;
    }
}

function error_func($buf, $what, $arg)
{
    /* Gestion de l'erreur */
}

$base = event_base_new();
$b = event_buffer_new(STDIN, "callback_func", NULL, "error_func", $base);

event_buffer_base_set($b, $base);
event_buffer_enable($b, EV_READ);

event_base_loop($base);

?>
