refentry.refsect1:
&reftitle.returnvalues;


 maxdb_field_seek retourne la valeur
 précédente du curseur de champ.

    &reftitle.examples;

     Style procédural


<?php
$link = maxdb_connect("localhost", "my_user", "my_password", "world");

/* Vérification de la connexion */
if (maxdb_connect_errno()) {
    printf("Echec de la connexion : %s\n", maxdb_connect_error());
    exit();
}

$query = "SELECT Name, SurfaceArea from Country ORDER BY Code";

if ($result = maxdb_query($link, $query)) {

    /* Récupération des informations de la deuxième colonne */
    maxdb_field_seek($result, 1);
    $finfo = maxdb_fetch_field($result);

    printf("Nom :     %s\n", $finfo->name);
    printf("Longueur max. : %d\n", $finfo->max_length);
    printf("Type :     %d\n\n", $finfo->type);

    maxdb_free_result($result);
}

/* Fermeture de la connexion */
maxdb_close($link);
?>


     &example.outputs;


Nom :     SurfaceArea
Longueur max. : 10
Type :     4
