refentry.refsect1:
&reftitle.returnvalues;


 maxdb_field_seek retourne
 la valeur précédante du curseur de champ.

  &reftitle.examples;

   Style orienté objet


<?php
$maxdb = new maxdb("localhost", "MONA", "RED", "DEMODB");

/* Vérification de la connexion */
if (maxdb_connect_errno()) {
    printf("Echec de la connexion : %s\n", maxdb_connect_error());
    exit();
}

$query = "SELECT name, cno from hotel.customer ORDER BY cno";

if ($result = $maxdb->query($query)) {

    /* Récupération des informations de champ pour la deuxième colonne */
    $result->field_seek(1);
    $finfo = $result->fetch_field();

    printf("Nom :     %s\n", $finfo->name);
    printf("Table :    %s\n", $finfo->table);
    printf("Longueur max. : %d\n", $finfo->max_length);
    printf("Flags :    %d\n", $finfo->flags);
    printf("Type :     %d\n\n", $finfo->type);

    $result->close();
}

/* Fermeture de la connexion */
$maxdb->close();
?>




   Style procédural


<?php
$link = maxdb_connect("localhost", "MONA", "RED", "DEMODB");

/* Vérification de la connexion */
if (maxdb_connect_errno()) {
    printf("Echec de la connexion : %s\n", maxdb_connect_error());
    exit();
}

$query = "SELECT name, cno from hotel.customer ORDER BY cno";

if ($result = maxdb_query($link, $query)) {

    /* Récupération des informations de champ pour la deuxième colonne */
    maxdb_field_seek($result, 1);
    $finfo = maxdb_fetch_field($result);

    printf("Nom :     %s\n", $finfo->name);
    printf("Table :    %s\n", $finfo->table);
    printf("Longueur max. : %d\n", $finfo->max_length);
    printf("Flags :    %d\n", $finfo->flags);
    printf("Type :     %d\n\n", $finfo->type);

    maxdb_free_result($result);
}

/* Fermeture de la connexion */
maxdb_close($link);
?>


   &example.outputs;


Nom :     NAME
Table :
Longueur max. : 10
Flags :    -1
Type :     2
