refentry.refsect1:
  &reftitle.examples;

   Style orienté objet


<?php
/* Ouvre une connexion */
$maxdb = new maxdb("localhost", "MONA", "RED", "DEMODB");

/* Vérification de la connexion */
if (maxdb_connect_errno()) {
    printf("Echec de la connexion : %s\n", maxdb_connect_error());
    exit();
}

$query = "SELECT name, zip FROM hotel.city ORDER BY name";
if ($stmt = $maxdb->prepare($query)) {

    /* Exécute une requête */
    $stmt->execute();

    /* Lie les variables de résultats */
    $stmt->bind_result($name, $code);

    /* Stock le résultat */
    $stmt->store_result();

    /* Se place sur la ligne no. 5 */
    $stmt->data_seek(5);

    /* Récupère les valeurs */
    $stmt->fetch();

    printf ("Ville : %s  Code postal : %s\n", $name, $code);

    /* Fermeture de la requête */
    $stmt->close();
}

/* Fermeture de la connexion */
$maxdb->close();
?>




   Style procédural


<?php
/* Ouvre une connexion */
$link = maxdb_connect("localhost", "MONA", "RED", "DEMODB");

/* Vérification de la connexion */
if (maxdb_connect_errno()) {
    printf("Echec de la connexion : %s\n", maxdb_connect_error());
    exit();
}

$query = "SELECT name, zip FROM hotel.city ORDER BY name";
if ($stmt = maxdb_prepare($link, $query)) {

    /* Exécute une requête */
    maxdb_stmt_execute($stmt);

    /* Lie les variables de résultats */
    maxdb_stmt_bind_result($stmt, $name, $code);

    /* Stock le résultat */
    maxdb_stmt_store_result($stmt);

    /* Se place sur la ligne no. 5 */
    maxdb_stmt_data_seek($stmt, 5);

    /* Récupère les valeurs */
    maxdb_stmt_fetch($stmt);

    printf ("Ville : %s  Code postal : %s\n", $name, $code);

    /* Fermeture de la requête */
    maxdb_stmt_close($stmt);
}

/* Fermeture de la connexion */
maxdb_close($link);
?>



