refentry.refsect1:
&reftitle.returnvalues;

 Valeurs retournées




    Valeur
    Description





    &true;
    Succès. Les données ont été récupérées



    &false;
    Une erreur est survenue



    &null;
    Il n'y a plus de lignes/données disponibles

  &reftitle.examples;

   Style orienté objet


<?php
$maxdb = new maxdb("localhost", "MONA", "RED", "DEMODB");

/* Vérification de la connexion */
if (maxdb_connect_errno()) {
    printf("Echec de la connexion : %s\n", maxdb_connect_error());
    exit();
}

$query = "SELECT zip, name FROM hotel.city ORDER by name";

if ($stmt = $maxdb->prepare($query)) {

    /* Exécution de la requête */
    $stmt->execute();

    /* Lie les variables de résultats */
    $stmt->bind_result($name, $code);

    /* Récupération des valeurs */
    while ($stmt->fetch()) {
        printf ("%s (%s)\n", $name, $code);
    }

    /* Fermeture de la requête */
    $stmt->close();
}

/* Fermeture de la connexion */
$maxdb->close();
?>




   Style procédural


<?php
$link = maxdb_connect("localhost", "MONA", "RED", "DEMODB");

/* Vérification de la connexion */
if (maxdb_connect_errno()) {
    printf("Echec de la connexion : %s\n", maxdb_connect_error());
    exit();
}

$query = "SELECT zip, name FROM hotel.city ORDER by name";

if ($stmt = maxdb_prepare($link, $query)) {

    /* Exécution de la requête */
    maxdb_stmt_execute($stmt);

    /* Lie les variables de résultats */
    maxdb_stmt_bind_result($stmt, $name, $code);

    /* Récupération des valeurs */
    while (maxdb_stmt_fetch($stmt)) {
        printf ("%s (%s)\n", $name, $code);
    }

    /* Fermeture de la requête */
    maxdb_stmt_close($stmt);
}

/* Fermeture de la connexion */
maxdb_close($link);
?>


