refsect1.para:
     L'exemple suivant montre comment on peut lister les informations
     disponibles :

      Exemple avec get_browser


<?php
echo $_SERVER['HTTP_USER_AGENT'] . "<hr />\n";

$browser = get_browser();

foreach ($browser as $name => $value) {
    print "<strong>$name</strong> $value <br />\n";
}

?>
