      Exemple avec mysql_list_dbs


<?php
$link = mysql_connect('localhost', 'utilisateur_mysql', 'mot_de_passe_mysql');
$db_list = mysql_list_dbs($link);

while ($row = mysql_fetch_object($db_list)) {
    echo $row->Database . "\n";
}
?>


