refentry.refsect1:
&reftitle.parameters;



  &mysqli.link.description;

   mode



     Si l'on doit active ou non l'auto-commit.

&reftitle.notes;



  Cette fonction ne fonctionne pas avec les types de tables non
  transactionnelles. (Comme MyISAM ou ISAM.)

  &reftitle.examples;

   Style orienté objet


<?php
$mysqli = new mysqli("localhost", "my_user", "my_password", "world");

if (mysqli_connect_errno()) {
    printf("Échec de la connexion. Erreur : %s\n", mysqli_connect_error());
    exit();
}

/* Activation de l'autocommit */
$mysqli->autocommit(TRUE);

if ($result = $mysqli->query("SELECT @@autocommit")) {
    $row = $result->fetch_row();
    printf("L'autocommit vaut %s\n", $row[0]);
    $result->free();
}

/* Fermeture de la connexion */
$mysqli->close();
?>




   Style procédural


<?php
$link = mysqli_connect("localhost", "my_user", "my_password", "world");

if (!$link) {
    printf("Échec de la connexion. Erreur : %s\n", mysqli_connect_error());
    exit();
}

/* Activation de l'autocommit */
mysqli_autocommit($link, TRUE);

if ($result = mysqli_query($link, "SELECT @@autocommit")) {
    $row = mysqli_fetch_row($result);
    printf("L'autocommit vaut %s\n", $row[0]);
    mysqli_free_result($result);
}

/* Fermeture de la connexion */
mysqli_close($link);
?>



  &example.outputs;


L'autocommit vaut 1
