refentry.refsect1:
    &reftitle.examples;

      Style orienté objet


<?php

/* Connexion à la base de données test */
$mysqli = new mysqli("localhost", "my_user", "my_password", "test");

/* Vérification de la connexion */
if (mysqli_connect_errno()) {
    printf("Echec de la connexion : %s\n", mysqli_connect_error());
    exit();
}

/* Définition de la variable "a" */
$mysqli->query("SET @a:=1");

/* Réinitialisation et changement de l'utilisateur */
$mysqli->change_user("my_user", "my_password", "world");

if ($result = $mysqli->query("SELECT DATABASE()")) {
    $row = $result->fetch_row();
    printf("Base de données par défaut : %s\n", $row[0]);
    $result->close();
}

if ($result = $mysqli->query("SELECT @a")) {
    $row = $result->fetch_row();
    if ($row[0] === NULL) {
        echo "La valeur de la variable a est NULL\n";
    }
    $result->close();
}

/* Fermeture de la connexion */
$mysqli->close();
?>




      Procedural style


<?php
/* Connexion à la base de données test */
$link = mysqli_connect("localhost", "my_user", "my_password", "test");

/* Vérification de la connexion */
if (!$link) {
    printf("Echec de la connexion : %s\n", mysqli_connect_error());
    exit();
}

/* Définition de la variable "a" */
mysqli_query($link, "SET @a:=1");

/* Réinitialisation et changement de l'utilisateur */
mysqli_change_user($link, "my_user", "my_password", "world");

if ($result = mysqli_query($link, "SELECT DATABASE()")) {
    $row = mysqli_fetch_row($result);
    printf("Base de données par défaut : %s\n", $row[0]);
    mysqli_free_result($result);
}

if ($result = mysqli_query($link, "SELECT @a")) {
    $row = mysqli_fetch_row($result);
    if ($row[0] === NULL) {
        echo "La valeur de la variable a est NULL\n";
    }
    mysqli_free_result($result);
}

/* Fermeture de la connexion */
mysqli_close($link);
?>



    &example.outputs;


Base de données par défaut : world
La valeur de la variable a est NULL
