refentry.refsect1:
&reftitle.description;
 &title.procedural;

  arraymysqli_fetch_fields
  mysqli_resultresult

 &title.oop;

  mysqli_result

   arrayfetch_fields





 Cette fonction fonctionne comme mysqli_fetch_field
 à la différence que, au lieu de retourner un objet à la fois 
 pour chaque champ, les colonnes sont retournées en tant que tableau d'objets.

    &reftitle.examples;

     Style orienté objet


<?php
$mysqli = new mysqli("localhost", "my_user", "my_password", "world");

/* Vérification de la connexion */
if (mysqli_connect_errno()) {
    printf("Echec de la connexion : %s\n", mysqli_connect_error());
    exit();
}

$query = "SELECT Name, SurfaceArea from Country ORDER BY Code LIMIT 5";

if ($result = $mysqli->query($query)) {

    /* Récupération des données métas pour toutes les colonnes */
    $finfo = $result->fetch_fields();

    for ($i=0; $i < count($finfo); $i++) { 
        printf("Nom :     %s\n", $finfo[$i]->name);
        printf("Table :    %s\n", $finfo[$i]->table);
        printf("Longueur Max. : %d\n", $finfo[$i]->max_length);
        printf("Flags :    %d\n", $finfo[$i]->flags);
        printf("Type :     %d\n\n", $finfo[$i]->type);
    }    
    $result->close();
}

/* Fermeture de la connexion */
$mysqli->close();
?>




     Style procédural


<?php
$link = mysqli_connect("localhost", "my_user", "my_password", "world");

/* Vérification de la connexion */
if (mysqli_connect_errno()) {
    printf("Echec de la connexion : %s\n", mysqli_connect_error());
    exit();
}

$query = "SELECT Name, SurfaceArea from Country ORDER BY Code LIMIT 5";

if ($result = mysqli_query($link, $query)) {

    /* Récupération des données métas pour toutes les colonnes */
    $finfo = mysqli_fetch_fields($result);

    for ($i=0; $i < count($finfo); $i++) { 
        printf("Nom :     %s\n", $finfo[$i]->name);
        printf("Table :    %s\n", $finfo[$i]->table);
        printf("Longueur Max. : %d\n", $finfo[$i]->max_length);
        printf("Flags :    %d\n", $finfo[$i]->flags);
        printf("Type :     %d\n\n", $finfo[$i]->type);
    }    
    mysqli_free_result($result);
}

/* Fermeture de la connexion */
mysqli_close($link);
?>



