refentry.refsect1:
    &reftitle.examples;



      Exemple avec mysqli_report


<?php
/* Active le rapport d'erreur mysqli */
mysqli_report(MYSQLI_REPORT_ALL);

$mysqli = new mysqli("localhost", "utilisateur", "mot_de_passe", "base");

/* Vérifie la connexion */
if (mysqli_connect_errno()) {
    printf("Connexion échouée : %s\n", mysqli_connect_error());
    exit();
}

/* Cette requête doit générer une erreur */
$result = $mysqli->query("SELECT Nom FROM Table_non_existante WHERE population > 50000");

/* Cette requête doit générer une alerte */
$result = $mysqli->query("SELECT Nom FROM Ville WHERE population > 50000");
$result->close();

$mysqli->close();
?>
