refentry.refsect1:
    &reftitle.examples;

     Style orientié objet


<?php
$mysqli = new mysqli("localhost", "utilisateur", "mot_de_passe", "base");

if (mysqli_connect_errno()) {
    printf("Connexion échouée : %s\n", mysqli_connect_error());
    exit();
}

/* prépare la requête */
if ($stmt = $mysqli->prepare("SELECT Code, Nom FROM Pays ORDER BY Name LIMIT 5")) {
    $stmt->execute();

    /* Association des variables au résultat */
    $stmt->bind_result($col1, $col2);

    /* Lecture des valeurs */
    while ($stmt->fetch()) {
        printf("%s %s\n", $col1, $col2);
    }

    /* Fin du résultat */
    $stmt->close();
}
/* Fermeture de la connexion */
$mysqli->close();

?>




     Style procédural


<?php
$link = mysqli_connect("localhost", "utilisateur", "mot_de_passe", "base");

/* Vérification de la connexion */
if (!$link) {
    printf("Connexion échouée : %s\n", mysqli_connect_error());
    exit();
}

/* Preparation de la requête */
if ($stmt = mysqli_prepare($link, "SELECT Code, Nom FROM Pays ORDER BY Name LIMIT 5")) {
    mysqli_stmt_execute($stmt);

    /* Association des variables au résultat */
    mysqli_stmt_bind_result($stmt, $col1, $col2);

    /* Lecture des valeurs */
    while (mysqli_stmt_fetch($stmt)) {
       printf("%s %s\n", $col1, $col2);
    }

    /* Fermeture de la requête */
    mysqli_stmt_close($stmt);
}

/* Fermeture de la connexion */
mysqli_close($link);
?>



