refentry.refsect1:
&reftitle.description; &title.procedural;
voidmysqli_stmt_data_seek mysqli_stmtstmt intoffset
&title.oop;
mysqli_stmt
voiddata_seek intoffset
Déplace le pointeur du résultat statement de offset lignes.

&reftitle.parameters;



  &mysqli.stmt.description;

   offset



     Doit prendre une valeur entre zéro et le nombre total de ligne moins 1
     (0..mysqli_stmt_num_rows - 1).

  &reftitle.examples;

   Style orienté objet


<?php
/* Ouvre la connexion */
$mysqli = new mysqli("localhost", "my_user", "my_password", "world");

/* Vérifie la connexion */
if (mysqli_connect_errno()) {
    printf("Échec de la connexion : %s\n", mysqli_connect_error());
    exit();
}

$query = "SELECT Nom, Pays FROM Ville ORDER BY Name";
if ($stmt = $mysqli->prepare($query)) {

    /* Exécute la requête */
    $stmt->execute();

    /* Lie les variables de résultat */
    $stmt->bind_result($name, $code);

    /* Lit le résultat */
    $stmt->store_result();

    /* Va à la ligne 400 */
    $stmt->data_seek(399);

    /* Lit une valeur */
    $stmt->fetch();

    printf ("Ville : %s  Pays : %s\n", $name, $code);

    /* Ferme la commande */
    $stmt->close();
}

/* Ferme la connexion */
$mysqli->close();
?>




   Style procédural


<?php
/* Ouvre la connexion */
$link = mysqli_connect("localhost", "my_user", "my_password", "world");

/* Vérifie la connexion */
if (mysqli_connect_errno()) {
    printf("Échec de la connexion : %s\n", mysqli_connect_error());
    exit();
}

$query = "SELECT Nom, Pays FROM Ville ORDER BY Name";
if ($stmt = mysqli_prepare($link, $query)) {

    /* Exécute la requête */
    mysqli_stmt_execute($stmt);

    /* Lie les variables de résultat */
    mysqli_stmt_bind_result($stmt, $name, $code);

    /* Lit le résultat */
    mysqli_stmt_store_result($stmt);

    /* Va à la ligne 400 */
    mysqli_stmt_data_seek($stmt, 399);

    /* Lit une valeur */
    mysqli_stmt_fetch($stmt);

    printf ("Ville : %s  Pays : %s\n", $name, $code);

    /* Ferme la commande */
    mysqli_stmt_close($stmt);
}

/* Ferme la connexion */
mysqli_close($link);
?>



  &example.outputs;


Ville : Benin City  Pays : NGA
