&reftitle.returnvalues;

 &reftitle.returnvalues;




    Valeur
    Description





    &true;
    Réussite. Les données ont été lues.



    &false;
    Une erreur est survenue.



    &null;
    Il n'y a plus de ligne à lire

    &reftitle.examples;

     Style orienté objet


<?php
/* Ouvre la connexion */
$mysqli = new mysqli("localhost", "utilisateur", "mot_de_passe", "base");

/* Vérifie la connection */ 
if (mysqli_connect_errno()) {
    printf("Connexion échouée : %s\n", mysqli_connect_error());
    exit();
}

$query = "SELECT Nom, Pays FROM Ville ORDER by ID DESC LIMIT 150,5";

if ($stmt = $mysqli->prepare($query)) {

    /* Execution de la requête */
    $stmt->execute();

    /* Association des variables de résultat */
    $stmt->bind_result($name, $code);

    /* Lecture des valeurs */
    while ($stmt->fetch()) {
        printf ("%s (%s)\n", $name, $code);
    }

    /* Fermeture de la commande */
    $stmt->close();
}

/* Fermeture de la connexion */
$mysqli->close();
?>




     Style procédural


<?php
/* Ouvre la connexion */
$link = mysqli_connect("localhost", "utilisateur", "mot_de_passe", "base");

/* Vérifie la connection */ 
if (mysqli_connect_errno()) {
    printf("Connexion échouée : %s\n", mysqli_connect_error());
    exit();
}

$query = "SELECT Nom, Pays FROM Ville ORDER by ID DESC LIMIT 150,5";

if ($stmt = mysqli_prepare($link, $query)) {

    /* Exécution */
    mysqli_stmt_execute($stmt);

    /* Association des variables de résultat */
    mysqli_stmt_bind_result($stmt, $name, $code);

    /* Lecture des valeurs */
    while (mysqli_stmt_fetch($stmt)) {
        printf ("%s (%s)\n", $name, $code);
    }

    /* Fermeture de la commande */
    mysqli_stmt_close($stmt);
}

/* Fermeture de la connexion */
mysqli_close($link);
?>



