refentry.refsect1:
    &reftitle.examples;



      Exemple d'interface de callback Readline


<?php
function rl_callback($ret)
{
    global $c, $prompting;

    echo "Votre entrée : $ret\n";
    $c++;

    if ($c > 10) {
        $prompting = false;
        readline_callback_handler_remove();
    } else {
        readline_callback_handler_install("[$c] Enter something: ", 'rl_callback');
    }
}

$c = 1;
$prompting = true;

readline_callback_handler_install("[$c] Enter something: ", 'rl_callback');

while ($prompting) {
    $n = stream_select($r = array(STDIN), $w = null, $e = null, null);
    if ($n && in_array(STDIN, $r)) {
        // lit un caractère, appelera la fonction de callback lorsqu'une nouvelle ligne est entrée
        readline_callback_read_char();
    }
}

echo "Le prompt est désactivé. Tout a été effectué.\n";
?>
