refentry.refsect1:
&reftitle.description;

 boolrunkit_method_redefine
 stringclassname
 stringmethodname
 stringargs
 stringcode
 intflagsRUNKIT_ACC_PUBLIC

&note.runkit.selfmanipulation;
&warn.experimental.func;

  &reftitle.examples;



    Exemple avec runkit_method_redefine


<?php
class Example {
    function foo() {
        return "foo!\n";
    }
}

// Crée un objet Example
$e = new Example();

// Sortie Example::foo() (avant la redéfinition)
echo "Avant : " . $e->foo();

// Redéfinition de la méthode 'foo'
runkit_method_redefine(
    'Example',
    'foo',
    '',
    'return "bar!\n";',
    RUNKIT_ACC_PUBLIC
);

// Sortie Example::foo() (après la redéfinition)
echo "Après : " . $e->foo();
?>


    &example.outputs;


Avant : foo!
Après : bar!
