    Valider une transaction SESAM


<?php
if (sesam_connect ("mycatalog", "myschema", "otto")) {
    if (!sesam_execimm ("INSERT INTO mytable VALUES (*, 'Small Test', <0, 8, 15>)"))
        die("Échec de l'insertion");
    if (!sesam_commit())
        die("Échec de l'insertion");
}
?>
