    Annulation d'une transaction SESAM


<?php
if (sesam_connect ("moncatalogue", "monschema", "toto")) {
    if (sesam_execimm ("INSERT INTO matable VALUES (*, 'Petit Test', <0, 8, 15>)")
        && sesam_execimm ("INSERT INTO autretable VALUES (*, 'Autre Test', 1)")) {
        sesam_commit();
    } else {
        sesam_rollback();
    }
}
?>
