refentry.refsect1:
  &reftitle.description;

   arraysqlite_fetch_all
   resourceresult
   intresult_type
   booldecode_binary

  &title.oop;

   SQLiteResult

    arrayfetchAll
    intresult_type
    booldecode_binary



   SQLiteUnbuffered

    arrayfetchAll
    intresult_type
    booldecode_binary




   sqlite_fetch_all retourne un tableau contenant
   le jeu de résultats complet depuis la ressource result.
   Cela revient à appeler la fonction sqlite_query (ou
   sqlite_unbuffered_query) puis la fonction
   sqlite_fetch_array pour chaqu'une des lignes du jeu de 
   résultats.

  &sqlite.result-type;
  &sqlite.case-fold;
  &sqlite.decode-bin;



    Exemple avec sqlite_fetch_all


<?php
$dbhandle = sqlite_open('sqlitedb');
$query = sqlite_query($dbhandle, 'SELECT name, email FROM users LIMIT 25');
$result = sqlite_fetch_all($query, SQLITE_ASSOC);
foreach ($result as $entry) {
    echo 'Nom : ' . $entry['name'] . '  E-mail : ' . $entry['email'];
}

/* Exemple OO */
$dbhandle = new SQLiteDatabase('sqlitedb');

$query = $dbhandle->query('SELECT name, email FROM users LIMIT 25'); // jeu de résultat bufferisé
$query = $dbhandle->unbufferedQuery('SELECT name, email FROM users LIMIT 25'); // jeu de résultat non bufferisé

$result = $query->fetchAll(SQLITE_ASSOC);
foreach ($result as $entry) {
    echo 'Nom : ' . $entry['name'] . '  E-mail : ' . $entry['email'];
}
?>
