para.example:
      Utilisation de stripslashes sur un tableau


<?php
function stripslashes_deep($value)
{
    $value = is_array($value) ?
                array_map('stripslashes_deep', $value) :
                stripslashes($value);

    return $value;
}

// Exemple
$array = array('f\'oo', 'b\'ar', array('fo\'o', 'b\'ar'));
$array = stripslashes_deep($array);

// Affichage
print_r($array);
?>


      &example.outputs;


Array
(
    [0] => f'oo
    [1] => b'ar
    [2] => Array
        (
            [0] => fo'o
            [1] => b'ar
        )

)
