  &reftitle.examples;



    Exemple avec SwishSearch->setLimit


<?php
try {

    $swish = new Swish("index.swish-e");
    $search = $swish->prepare();

    $results = $search->execute("time");
    echo "Première requête trouvée : ", $results->hits, " résultats\n";

    $i = 0;
    while($result = $results->nextResult()) {
        echo "Résultat #", ++$i, " - ", $result->swishdocsize, " octets\n";
    }

    $search->setLimit("swishdocsize", "3000", "6000"); // limite par la taille du document, entre 3000 et 6000 octets
    $results = $search->execute("time");
    echo "Seconde requête trouvée : ", $results->hits, " résultats\n";

    $i = 0;
    while($result = $results->nextResult()) {
        echo "Résultat #", ++$i, " - ", $result->swishdocsize, " octets\n";
    }

} catch (SwishException $e) {
    echo $e->getMessage(), "\n";
}

?>


