    &title.oop;

     booltidy->parseFile
     stringfilename
     mixedconfig
     stringencoding
     booluse_include_path



     tidy_parse_file analyse le fichier spécifié.

    &tidy.conf-enc;



      Exemple avec tidy_parse_file


<?php
$tidy = tidy_parse_file('file.html');

$tidy->cleanRepair();

if(!empty($tidy->error_buf)) {
    echo 'Les erreurs et avertissements suivants ont été recontrés :'."\n";
    echo $tidy->error_buf;
}
?>




